import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js"
import Message from '../components/Message'
import Loader from '../components/Loader'
import { getOrderDetails, payOrder } from '../actions/orderAction'
import { ORDER_PAY_RESET } from '../constants/orderConstants'
const initialOptions = {
  clientId: "AZ2x-OM0xtqVb_Up8sobCdse8RJHmejml6pf2xKbC-cBq2s00-UfpyoYnzhCkLPAd1jSVkKTQrxrMcB9",
  currency: "USD",
  intent: "capture",
};



function OrderScreen() {

    const { id } = useParams();    
    const dispatch = useDispatch()

    const [sdkReady, setSdkReady] = useState(false)

    const orderDetails = useSelector(state =>  state.orderDetails)
    const { order, error, loading } = orderDetails

    const orderPay = useSelector(state =>  state.orderPay)
    const { loading: loadingPay, success: successPay } = orderPay



    if (!loading && !error){
        order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
    }

    //AZ2x-OM0xtqVb_Up8sobCdse8RJHmejml6pf2xKbC-cBq2s00-UfpyoYnzhCkLPAd1jSVkKTQrxrMcB9

    //EAPRONJAEMsbzzrAtszO2qzrcaOZ74lQ0HbDpE3u2ZErWvyWqVe4fEQygQEBFwVXmPW71rBLoCu-rsa_

    // Add Paypal Script

    // const addPayPalScript = () => {
    //     const script = document.createElement('script')
    //     script.type = 'text/javascript'
    //     script.src = 'https://www.paypal.com/sdk/js?client-id=AZ2x-OM0xtqVb_Up8sobCdse8RJHmejml6pf2xKbC-cBq2s00-UfpyoYnzhCkLPAd1jSVkKTQrxrMcB9&currency=USD"'
    //     script.async = true
    //     script.onload = () => {
    //         setSdkReady(true)
    //     }
    //     document.body.appendChild(script)
    // }


    useEffect(() => {
        if (!order || orderPay || order._id !== Number(id)){
            dispatch({type: ORDER_PAY_RESET})
            dispatch(getOrderDetails(id))
        }else if(!order.isPaid) {
            if (!window.paypal){
                initialOptions()
            }else{
                setSdkReady(true)
            }
        }

    }, [dispatch, order, id, successPay])



    const successPaymentHandler = (paymentResult) => {
        dispatch(payOrder(id, paymentResult))
    }



  return loading ? ( <Loader /> ) : error ? ( 
    <Message variant='danger'>{error}</Message>
  ) : (
    <div>
        <h1>Order: {order._id}</h1>
        <Row>
            <Col md={8}>
                <ListGroup variant='flush'>
                    <ListGroup.Item>
                        <h2>Shipping</h2>

                        <p><strong>Name:</strong>{order.user.name}</p>
                        <p><strong>Email: </strong><a href={`mailto:${order.user.email}`}>{order.user.email}</a></p>

                        <p>
                            <strong>Shipping: </strong>
                            {order.shippingAddress.address}, {order.shippingAddress.city}
                            {'  '}
                            {order.shippingAddress.postalCode},
                            {'  '}
                            {order.shippingAddress.country} 
                        </p>

                        {order.isDelivered ? (
                            <Message variant='success'>Delivered on {order.deliveredAt}</Message>
                        ) : (
                            <Message variant='warning'>Not Delivered</Message> 
                        )}


                    </ListGroup.Item>

                    <ListGroup.Item>
                        <h2>Payment Method</h2>

                        <p>
                            <strong>Method: </strong>
                            {order.paymentMethod} 
                        </p>

                        {order.isPaid ? (
                            <Message variant='success'>Pain on {order.paidAt}</Message>
                        ) : (
                            <Message variant='warning'>Not Paid</Message> 
                        )}

                    </ListGroup.Item>


                    <ListGroup.Item>
                        <h2>Order Items</h2>

                        {order.orderItems.length === 0 ? <Message variant='info'> 
                            Order is empty
                        </Message> : (
                            <ListGroup variant='flush'>
                                {order.orderItems.map((item, index) => (
                                    <ListGroup.Item key={index}>
                                        <Row>
                                            <Col md={1}>
                                                <Image src={item.image} alt={item.name} fluid rounded />
                                            </Col>

                                            <Col>
                                                <Link to={`/product/${item.product}`}>{item.name}</Link>
                                            </Col>

                                            <Col md={4}>
                                                {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>
                        )}
                    </ListGroup.Item>


                </ListGroup>
            </Col>

            <Col md={4}>
                <Card>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Order Summary</h2>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Item:</Col>
                                <Col>${order.itemsPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Shipping:</Col>
                                <Col>${order.shippingPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Tax:</Col>
                                <Col>${order.taxPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                                <Col>Total:</Col>
                                <Col>${order.totalPrice}</Col>
                            </Row>
                        </ListGroup.Item>    

                        {!order.isPaid && (
                            <ListGroup.Item>
                                { loadingPay && <Loader /> }

                                {!sdkReady ? (
                                    <Loader />
                                ) : (
                                    // <PayPalButtons amount={order.totalPrice} onSuccess={successPaymentHandler} />
                                    <PayPalScriptProvider options={initialOptions}>
                                        <PayPalButtons />
                                    </PayPalScriptProvider>
                                )}
                            </ListGroup.Item>
                        )}      

                    </ListGroup>
                </Card>
            </Col>
        </Row>

    </div>
  )
}

export default OrderScreen
