import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useLocation } from 'react-router-dom'
import { Row, Col } from 'react-bootstrap'
import Product from '../components/Product'
import { listProducts } from '../actions/productActions'
import Loader from '../components/Loader'
import Message from '../components/Message'
import Paginate from '../components/Paginate'



function HomeScreen() {
    // const dispatch = useDispatch()
    // const location = useLocation();

    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)
    const { error, loading, products, pages } = productList

    const [searchParams] = useSearchParams()
    const page = Number(searchParams.get('page')) || 1

    useEffect(() => {
        dispatch(listProducts(searchParams.toString()))
    }, [dispatch, searchParams])

    // const productList = useSelector(state => state.productList)
    // const { error, loading, products, page, pages } = productList

    // let keyword = location.search;



    // useEffect(() => {
    //     dispatch(listProducts(keyword))       

    // }, [dispatch, keyword])

    console.log("Loading:", loading); // Check the value of loading here
    console.log("Error:", error); // Check the value of error here


return (
    <div> 
    <h1>Latest Products</h1>
    {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    :
                    <div>
                        <Row>
                            {products.map(product => (
                                <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                                    <Product product={product} />
                                </Col>
                            ))}
                        </Row>
                        <Paginate page={page} pages={pages} keyword={searchParams.toString()} />
                    </div>
            }

    </div> 
)
}

export default HomeScreen
