import React, { useEffect } from 'react'
import { Link, useNavigate, useParams, useLocation  } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import Message from '../components/Message'
import { addToCart } from '../actions/cartAction'

function CartScreen({ history }) {

  // Use hooks from React Router v6 instead of 'match' and 'location'
  const params = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  const { id } = useParams()
  const qty = new URLSearchParams(location.search).get('qty') ? Number(location.search.split('=')[1]) : 1

  const dispatch = useDispatch()

  const cart = useSelector(state => state.cart)
  const { cartItems } = cart


  useEffect(() => {
    if(id){
      dispatch(addToCart(id, qty))
    }
  })

  const handleQuantityChange = (item, qty) => {
    dispatch(addToCart(item.product, qty));
  };

  return (  
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <Message variant='info'>
            Your cart is empty <Link to='/'>Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
              {cartItems.map(item => (
                <ListGroup.Item key={item.product}>
                  <Row>
                    <Col md={2}>
                      <Image src={item.image} alt={item.name} fluid rounded />
                    </Col>
                    <Col md={3}>
                      <Link to={`/product/${item.product}`}>{item.name}</Link>
                    </Col>
                    <Col md={2}>
                      ${item.price}
                    </Col>
                    <Col md={3}>
                      <Form.Control as="select" value={item.qty} onChange={(e) => handleQuantityChange(item, Number(e.target.value))}>
                        {
                            [...Array(item.countInStock).keys()].map((x) => (
                                <option key={x + 1} value={x + 1}>
                                    {x + 1}
                                </option>
                            ))
                        }
                      </Form.Control>
                    </Col>
                  </Row>
                </ListGroup.Item>
              ))}
          </ListGroup>
        )

        }
      </Col>

      <Col md={4}>

      </Col>
    </Row>
  )
}

export default CartScreen
